"use client"

import { useState } from "react"
import { Send, MessageSquare } from "lucide-react"
import { Textarea } from "@/components/ui/textarea"
import { useAutoResizeTextarea } from "@/hooks/use-auto-resize-textarea"
import { cn } from "@/lib/utils"

export default function AIInput_10() {
  const { textareaRef, adjustHeight } = useAutoResizeTextarea({ minHeight: 40, maxHeight: 200 })
  const [value, setValue] = useState("")
  const [expanded, setExpanded] = useState(false)

  const onSubmit = () => {
    setValue("")
    adjustHeight(true)
    setExpanded(false)
  }

  return (
    <div className="w-full py-4">
      <div className="max-w-xl mx-auto w-full space-y-2">
        {!expanded && !value ? (
          <button
            type="button"
            onClick={() => setExpanded(true)}
            className="w-full inline-flex items-center gap-2 rounded-full bg-black/5 dark:bg-white/5 px-4 py-2 text-sm text-black/70 dark:text-white/70"
          >
            <MessageSquare className="h-4 w-4" />
            Start a prompt…
          </button>
        ) : null}

        <div
          className={cn("relative transition-all", expanded || value ? "opacity-100" : "opacity-0 pointer-events-none")}
        >
          <Textarea
            aria-label="Expandable composer"
            ref={textareaRef}
            autoFocus
            value={value}
            onChange={(e) => {
              setValue(e.target.value)
              adjustHeight()
            }}
            onKeyDown={(e) => {
              if (e.key === "Enter" && !e.shiftKey) {
                e.preventDefault()
                onSubmit()
              }
            }}
            onBlur={() => {
              if (!value) setExpanded(false)
            }}
            placeholder="Write your idea…"
            className="rounded-2xl bg-black/5 dark:bg-white/5 border-none px-4 pr-12 py-3 min-h-[40px] max-h-[200px] focus-visible:ring-0 focus-visible:ring-offset-0"
          />
          <button
            onClick={onSubmit}
            disabled={!value}
            type="button"
            className={cn(
              "absolute right-2 top-1/2 -translate-y-1/2 rounded-xl p-2",
              value ? "bg-blue-600 text-white" : "bg-black/5 dark:bg-white/5 text-black/50 dark:text-white/50",
            )}
            aria-label="Send"
          >
            <Send className="h-4 w-4" />
          </button>
        </div>

        <div className="flex gap-2 flex-wrap">
          {["Brainstorm names", "Draft announcement", "Outline plan"].map((s) => (
            <button
              key={s}
              onClick={() => {
                setExpanded(true)
                setValue(s)
              }}
              type="button"
              className="rounded-full bg-black/5 dark:bg-white/5 px-3 py-1.5 text-sm"
            >
              {s}
            </button>
          ))}
        </div>
      </div>
    </div>
  )
}

import { useEffect, useRef, useCallback } from "react";

interface UseAutoResizeTextareaProps {
    minHeight: number;
    maxHeight?: number;
}

export function useAutoResizeTextarea({
    minHeight,
    maxHeight,
}: UseAutoResizeTextareaProps) {
    const textareaRef = useRef<HTMLTextAreaElement>(null);

    const adjustHeight = useCallback(
        (reset?: boolean) => {
            const textarea = textareaRef.current;
            if (!textarea) return;

            if (reset) {
                textarea.style.height = `${minHeight}px`;
                return;
            }

            // Temporarily shrink to get the right scrollHeight
            textarea.style.height = `${minHeight}px`;

            // Calculate new height
            const newHeight = Math.max(
                minHeight,
                Math.min(
                    textarea.scrollHeight,
                    maxHeight ?? Number.POSITIVE_INFINITY
                )
            );

            textarea.style.height = `${newHeight}px`;
        },
        [minHeight, maxHeight]
    );

    useEffect(() => {
        // Set initial height
        const textarea = textareaRef.current;
        if (textarea) {
            textarea.style.height = `${minHeight}px`;
        }
    }, [minHeight]);

    // Adjust height on window resize
    useEffect(() => {
        const handleResize = () => adjustHeight();
        window.addEventListener("resize", handleResize);
        return () => window.removeEventListener("resize", handleResize);
    }, [adjustHeight]);

    return { textareaRef, adjustHeight };
}

import { useState, useRef } from "react";

interface UseFileInputOptions {
    accept?: string;
    maxSize?: number;
}

export function useFileInput({ accept, maxSize }: UseFileInputOptions) {
    const [fileName, setFileName] = useState<string>("");
    const [error, setError] = useState<string>("");
    const fileInputRef = useRef<HTMLInputElement>(null);
    const [fileSize, setFileSize] = useState<number>(0);

    const handleFileSelect = (e: React.ChangeEvent<HTMLInputElement>) => {
        const file = e.target.files?.[0];
        validateAndSetFile(file);
    };

    const validateAndSetFile = (file: File | undefined) => {
        setError("");

        if (file) {
            if (maxSize && file.size > maxSize * 1024 * 1024) {
                setError(`File size must be less than ${maxSize}MB`);
                return;
            }

            if (
                accept &&
                !file.type.match(accept.replace("/*", "/"))
            ) {
                setError(`File type must be ${accept}`);
                return;
            }

            setFileSize(file.size);
            setFileName(file.name);
        }
    };

    const clearFile = () => {
        setFileName("");
        setError("");
        setFileSize(0);
        if (fileInputRef.current) {
            fileInputRef.current.value = "";
        }
    };

    return {
        fileName,
        error,
        fileInputRef,
        handleFileSelect,
        validateAndSetFile,
        clearFile,
        fileSize,
    };
}

import { useEffect, type RefObject } from "react";

export function useClickOutside(
    ref: RefObject<HTMLElement>,
    handler: () => void
) {
    useEffect(() => {
        const handleClickOutside = (event: MouseEvent) => {
            if (ref.current && !ref.current.contains(event.target as Node)) {
                handler();
            }
        };

        document.addEventListener("mousedown", handleClickOutside);
        return () =>
            document.removeEventListener("mousedown", handleClickOutside);
    }, [ref, handler]);
}
